        "labels = [0, 1, 0]  # Replace with your actual labels (1 for spam, 0 for not spam)\n",
        "\n",
        "# TF-IDF Vectorization\n",
        "vectorizer = TfidfVectorizer(max_features=5000)\n",
        "X_tfidf = vectorizer.fit_transform(texts).toarray()\n",
